	public function __construct()
	{
		$list = json_decode($this->_listJSON, true);
		foreach ($list['result'] as $key => $value) {
			// $this->_list[] = $value;
			$this->_list[$value['ID']] = $value;
		}
	}

	public function index()
	{
		$tasks = json_decode($this->_listJSON, true);
		foreach ($tasks["result"] as $task) {
			if ( $task["STATUS"] != static::STATUS_CLOSED ) {
				$list["result"][] = $task;
			}
		}
	    return json_encode($list, JSON_HEX_AMP);
	    // return $this->_listJSON;
	}    

	public function show($id)
	{
    	if ( isset($this->_list[$id]) ) {
    		$result = $this->_list[$id];
    		$status = 200;
    	} else {
    		$result = array();
    		$status = 404;
    	}
	    // return json_encode($this->_list[$id]);
	    return response()->json($result, $status);
	    // return response()->json($result, $status)->header('Content-Type', 'application/json');
	}    

